import React, { Component } from "react";
import ReactDOM from 'react-dom';

class NewExperience extends React.Component {
    constructor(props) {
      super(props)
      this.state = {
        currentStep: 1,
        photoFileName:  '',
        answer1: '',
        answer2: '', 
        answer3: '', 
      }
    }

    handleChange = event => {
      const {name, value} = event.target
      this.setState({
        [name]: value
      })    
    }

    handleSubmit = event => {
      event.preventDefault()
      const { photoFileName, answer1, answer2, answer3 } = this.state
      alert(`Your registration detail: \n 
             Selfie: ${photoFileName} \n 
             Answer1: ${answer1} \n
             Answer2: ${answer2} \n
             Answer3: ${answer3}`)
    }

    _next = () => {
      let currentStep = this.state.currentStep
      currentStep = currentStep >= 2? 3: currentStep + 1
      this.setState({
        currentStep: currentStep
      })
    }

    _prev = () => {
      let currentStep = this.state.currentStep
      currentStep = currentStep <= 1? 1: currentStep - 1
      this.setState({
        currentStep: currentStep
      })
    }

  /*
  * the functions for our button
  */
  previousButton() {
    let currentStep = this.state.currentStep;
    if(currentStep !==1){
      return (
        <button 
          className="btn btn-secondary" 
          type="button" onClick={this._prev}>
        Previous
        </button>
      )
    }
    return null;
  }

  nextButton(){
    let currentStep = this.state.currentStep;
    if(currentStep <3){
      return (
        <button 
          className="btn btn-primary float-right" 
          type="button" onClick={this._next}>
        Next
        </button>        
      )
    }
    return null;
  }

    render() {    
      return (
        <React.Fragment>
        <div className="col-md-12">
        <div className="card card-container login-form">
        <h1>Add New Experience</h1>
        <p>Step {this.state.currentStep} </p> 

        <form onSubmit={this.handleSubmit}>
        {/* 
          render the form steps and pass required props in
        */}
          <Step1 
            currentStep={this.state.currentStep} 
            handleChange={this.handleChange}
            photoFileName={this.state.photoFileName}
          />
          <Step2 
            currentStep={this.state.currentStep} 
            handleChange={this.handleChange}
            answer1={this.state.answer1}
            answer2={this.state.answer2}
            answer3={this.state.answer3}
          />
          {this.previousButton()}
          {this.nextButton()}

        </form>
        </div>
        </div>
        </React.Fragment>
      );
    }
  }

  function Step1(props) {
    if (props.currentStep !== 1) {
      return null
    } 
    return(
      <div className="form-group">
         <label class="cameraButton">Take a picture
    <input type="file" accept="image/*;capture=camera" />
  </label>


      </div>
    );
  }

  function Step2(props) {
    if (props.currentStep !== 2) {
      return null
    } 
    return(
      <div className="form-group">
        <label htmlFor="username">Username</label>
        <input
          className="form-control"
          id="username"
          name="username"
          type="text"
          placeholder="Enter username"
          value={props.username}
          onChange={props.handleChange}
          />
      </div>
    );
  }

  function Step3(props) {
    if (props.currentStep !== 3) {
      return null
    } 
    return(
      <React.Fragment>
      <div className="form-group">
        <label htmlFor="password">Password</label>
        <input
          className="form-control"
          id="password"
          name="password"
          type="password"
          placeholder="Enter password"
          value={props.password}
          onChange={props.handleChange}
          />      
      </div>
      <button className="btn btn-primary float-right">Register</button>
      </React.Fragment>
    );
  }

  export default NewExperience;
